<?php$host="localhost"; // Host name$username="smdtest1_loser"; // Mysql username$password="asdfasdf88"; // Mysql password$db_name="smdtest1_loser"; // Database name$tbl_name="user"; // Table name// Connect to server and select database.mysql_connect("$host", "$username", "$password")ordie("cannot connect");mysql_select_db("$db_name")or die("cannot select DB");// username and password sent from form$myemail=$_POST['myemail'];$mypassword=$_POST['mypassword'];// To protect MySQL injection (more detail about MySQL injection)$myemail = stripslashes($myemail);$mypassword = stripslashes($mypassword);$myemail = mysql_real_escape_string($myemail);$mypassword = mysql_real_escape_string($mypassword);$sql="SELECT * FROM $tbl_name WHERE email='$myemail' andpassword='$mypassword'";$result=mysql_query($sql);$count=mysql_num_rows($result);if($count==1){session_register("myemail");session_register("mypassword");header("location:index.php");}else {echo "Wrong Username or Password";}?>